// frontend/src/components/ViewDocument.js
import { useParams } from "react-router-dom";

export default function ViewDocument() {
  const { token } = useParams();
  const fileUrl = `http://localhost:5000/api/files/access/${token}`;

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>View Document</h2>
      <iframe
        src={fileUrl}
        title="Secure Document"
        width="90%"
        height="600px"
        style={{ border: "1px solid #ccc" }}
      ></iframe>
      <br /><br />
      <button onClick={() => window.frames[0].print()}>üñ®Ô∏è Print Document</button>
    </div>
  );
}
